import{p as l,P as d}from"./ProjectArrows-vvd1SmmW.js";import{S as h}from"./StyledImage-cXBYxl5l.js";import{b as u}from"./index-hKBLJ-EB.js";import{_ as c,c as p,b as n,a as e,r as s,e as g,o as m}from"./index-D2SOtV16.js";const b={components:{"project-header":d,"styled-image":h,"project-arrows":l},methods:{buildURL(a){return u(a)}}},_={id:"greenbox",class:"responsive"},f=e("h4",null,"TL;DR",-1),w=e("p",null,"As part of a university course a design team and a UX research team helped a startup to improve their gardening app. I contributed with planning, conducting and evaluating usability tests as part of the UX research team (3 persons in total).",-1),v=e("h4",null,"Problem",-1),y=e("p",null,[e("i",null,"Grüner Daumen"),g(' ("Green thumb") is an upcoming gardening app that is designed to inspire and support users with their plant care. The client had questions regarding the usability and design of their application:')],-1),x=e("ul",null,[e("li",null,"Is the workflow in agreement with the users' expectations?"),e("li",null,"Does the app feel inutitive and easy to use?"),e("li",null,"Do users perceive the app as playful and yet legitimate?")],-1),k=e("h4",null,"First usability test (my team)",-1),U=e("p",null,"In a first usability test participants were introduced to the app with a scenario and asked to perform several tasks while thinking aloud. We recorded the participant's interactions with the application with a camera and evaluated them later. The participants answered two questionnaires (QUESI, meCUE) rating the intuitiveness and the overall impression of the product. Additionally, the team conducted a heuristic analysis (following Jakob Nielsen) to identify usability problems. The insights generated from these methods were condensed and forwarded to the design team.",-1),T=e("h4",null,"Redesign (design team)",-1),D=e("p",null,"The design team used the collected insights from the testing and the heuristic analysis to produce a redesigned version of the app.",-1),j=e("h4",null,"Second usability test (my team)",-1),R=e("p",null,"In a second usability test the redesigned version was evaluated and the results compared to the results of the first version. The same methods were used as in the first usability test to allow for better comparison. Users experienced less problems with the redesigned version and rated it also higher on all scales of the questionnaires.",-1),A=e("h4",null,"Reflection",-1),B=e("p",null,"During this project I learnt a lot about the design, conduction and evaluation of usability tests. Experiencing that the insights generated through our group's work were of great use for the startup was a big motivational factor. The cooperation and communication between the design team, the UX research team and the startup was challenging, but rewarded us with different perspectives and insights in the process.",-1),I=e("h4",null,"Testimonial (german)",-1),L=e("p",null,'"Wir entwickeln momentan eine Smartphone-App und haben uns bei UseTree beworben, weil wir die Abfolge von Bedienschritten für eine innovative Anwendung evaluieren lassen wollten. Daneben war es uns wichtig, herauszufinden, inwieweit wir die Balance zwischen einer spielerischen Bedienung und einem seriösen Design hinbekommen. Das Zusammenspiel von Arbeitspsychologen und Designern im sogenannten Staffellauf, bei dem beide Seiten abwechselnd dieselbe Fragestellung bearbeiten, haben wir als sehr bereichernd und konstruktiv empfunden. Durch die Teilnahme wurde unser Blick für Usability-Fragestellungen maßgeblich geschärft. Zudem haben wir Tools an die Hand bekommen, um diese Fragen zukünftig inhouse weiter bearbeiten zu können. Das Designteam begeisterte uns mit kreativen und hochwertigen Vorschlägen, die wir direkt in die Weiterentwicklung aufnehmen. Nicht zuletzt war ich von der engagierten und offenen Art von UseTree angetan und empfand die Zusammenarbeit als sehr inspierierend." - Gesche Heitmann (Gründerin)',-1);function S(a,z,F,G,N,t){const r=s("project-header"),i=s("styled-image"),o=s("project-arrows");return m(),p("div",null,[n(r,{route:"/greenbox"}),e("div",_,[f,w,v,n(i,{src:t.buildURL("greenbox/greenbox_v1.jpg"),alt:"First version of the gardening app"},null,8,["src"]),y,x,k,n(i,{src:t.buildURL("greenbox/greenbox_app3.png"),alt:"Usability test with the gardening app"},null,8,["src"]),U,T,n(i,{src:t.buildURL("greenbox/greenbox_v2.jpg"),alt:"Redesigned version of the gardening app"},null,8,["src"]),D,j,R,A,n(i,{src:t.buildURL("greenbox/greenbox_presentation.png"),alt:"Final presentation Greenbox"},null,8,["src"]),B,I,L]),n(o,{route:"/greenbox"})])}const P=c(b,[["render",S]]);export{P as default};
