import{p as l,P as h}from"./ProjectArrows-CzT32TjW.js";import{S as c}from"./StyledImage-g8yxrW6z.js";import{b as u}from"./index-Bh5pGfok.js";import{_ as p,c as m,b as t,a as e,r as o,e as i,o as f}from"./index-CgvWWY5l.js";const w={components:{"project-header":h,"styled-image":c,"project-arrows":l},methods:{buildURL(a){return u(a)}}},g={id:"knimehub",class:"responsive body1"},_=e("h4",null,"TL;DR",-1),b=e("p",null,"I helped to build a website where data scientists can read up on existing functionality of the desktop application. This website also enables them to share and collaborate on data analysis projects.",-1),k=e("h4",null,"Introduction",-1),y=e("p",null,'KNIME develops a data science software which lets users create data analysis workflows with small blocks called "nodes" in a graphical programming paradigm. The community is big and vibrant and we wanted to give users a platform where they can help each other out and work together on general purpose solutions for common data science problems (e.g. churn prediction, anomaly detection). As the UX Designer on the team I was given the task to create a first prototype for the information architecture and the interaction design.',-1),v=e("h4",null,"User and task analysis",-1),I=e("p",null,"At first I wanted to find out users shared and collaborated with already existing tools. I interviewed 9 internal data scientists on their usage of similar products. I was especially interested in how they make their own work understandable to others and what helps them to understand other people's work (e.g. text descriptions, screenshots, oral explanations, visual cues).",-1),x=e("p",null,"From this research I identified challenges that were only poorly addressed by existing solutions. For example, a big challenge for users was the first time they try to use a new node. Sometimes the user did not know what the node could do, sometimes they were not sure how to set it up and sometimes they tried it out and it did not work the way they expected it to.",-1),U=e("p",null,"With these challenges in mind we decided on what user stories to focus and defined the most important features for the KNIME Hub:",-1),L=e("ul",null,[e("li",null,"Search for nodes: Users can search for nodes and determine if they are a good fit for their data science problem."),e("li",null,"Search for workflow: Users can search for example workflows that explain a specific data science problem."),e("li",null,"Install nodes: When users have found the right node, they can install it easily by drag-n-dropping it into the desktop application."),e("li",null,"Link nodes/workflows: Users can share links of node and workflow pages to provide other users with a nice way of referencing functionality in the desktop application.")],-1),R=e("h4",null,"Screen design",-1),S=e("p",null,"In this phase I ranked the content in importance and separated it onto different screens. I drew inspiration from online-communities where users share their content with others (node-red, chefkoch, youtube, soundcloud).",-1),j=e("p",null,"I created four pages: A landing page with search, a search results page, a page for nodes and one for workflows. I created links between the pages which should help the users to find what they need.",-1),N=e("p",null,"The first version of the wireframes were discussed in the team and additional content was added. In a second iteration I aimed for higher fidelity in the wireframes and focused more on the layout of the pages. This second version was used for communication to internal stakeholders.",-1),T=e("h4",null,"Final visual design and building",-1),E=e("p",null,"The final visual design was done by an external designer. I supported the development team during the building process and contributed by taking over small frontend tasks.",-1),M=e("h4",null,"Launch",-1),F=e("p",null,"The KNIME Hub was released and opened to the public during the KNIME Summit in March 2019. Since then it has received great interest and a growing community of active users. From March until October I assisted in the further development of features such as a deeper integration to the desktop application.",-1),K=e("p",null,[i("Check it out at "),e("a",{class:"link",href:"https://hub.knime.com"},"hub.knime.com"),i(".")],-1);function A(a,H,B,D,V,s){const r=o("project-header"),n=o("styled-image"),d=o("project-arrows");return f(),m("div",null,[t(r,{route:"/knimehub"}),e("div",g,[_,b,k,y,v,I,t(n,{src:s.buildURL("knimehub/quotes.png"),alt:"Quotes from the interviews",shadow:!1},null,8,["src"]),x,t(n,{src:s.buildURL("knimehub/node-issues.png"),alt:"User challenges with nodes",shadow:!1},null,8,["src"]),U,L,R,S,t(n,{src:s.buildURL("knimehub/communities.png"),alt:"Node-RED: a similar online community"},null,8,["src"]),j,t(n,{src:s.buildURL("knimehub/wireframes-rough.png"),alt:"First wireframes"},null,8,["src"]),N,t(n,{src:s.buildURL("knimehub/wireframes-refined.png"),alt:"Second, more refined wireframes"},null,8,["src"]),T,E,M,F,K,t(n,{src:s.buildURL("knimehub/screens-11-19.png"),alt:"Screens of the application as of Nov. 19"},null,8,["src"])]),t(d,{route:"/knimehub"})])}const O=p(w,[["render",A]]);export{O as default};
