import{p as l,P as c}from"./ProjectArrows-BZ8S2A5p.js";import{S as h}from"./StyledImage-BU-Vxond.js";import{b as d}from"./index-LVFgRGvP.js";import{_ as u,c as p,b as t,a as e,r as o,o as f}from"./index-CNHz_Ykd.js";const m={components:{"project-header":c,"styled-image":h,"project-arrows":l},methods:{buildURL(a){return d(a)}}},w={class:"responsive"},_=e("h4",null,"TL;DR",-1),g=e("p",null,"During my work for UseTree we tested the usability of an application with maintenance workers. The aim of the application is to allow for easy and sustainable transfer of knowledge.",-1),b=e("h4",null,"Origin",-1),v=e("p",null,"Our client developed an app that should help mechanics with the repair of subway engines. Mechanics can view different material (photo, video,...) on the repair procedure of an engine and even record video themselves. The leading question was if the application provided substantial help for the work of the mechanics.",-1),k=e("h4",null,"Field usability test",-1),y=e("p",null,"During a field test at a repair shop I conducted testings with two mechanics each lasting one hour. The test included a exploration phase of the application and an interview afterwards. The test focused on the future use case of the application and was aimed to generate insights into the work processes and the integration of the application in these processes. It was set in a challenging environment: Limited time of the worker, loud noises in the repair shop, sparse familiarity with usability testing of the worker.",-1),T=e("h4",null,"Lab usability test",-1),j=e("p",null,"An additional test in a usability lab focused on the usability aspects of the application. Four students participated in this study.",-1),U=e("h4",null,"Evaluation",-1),L=e("p",null,"The client was involved in all stages of test conduction and evaluation. This imparted basic usability competencies to the client and was aimed to integrate user-centered design in the client's workflow. The evaluation of the two tests generated valuable insights for the future development of the application. Several problems were identified regarding the integration of the app at the workplace and new ideas could be collected on how to achieve such an integration in a sustainable manner. The usability test unveiled room for improvement of the ease of use.",-1),x=e("h4",null,"Reflection",-1),R=e("p",null,"I carried out a majority of the field test planning and was the on-site examiner. Thus, I learnt a lot about the internal work processes of the mechanics in the repair shop. This project enhanced my knowledge of usability test design as the field test was set in a challenging environment. Finally, I learnt how to condense knowledge about the work situation, context of use and the functionality of the application to create a vision of a use case of maximum benefit.",-1),I=e("h4",null,"Testimonial (german)",-1),A=e("p",null,'"Mit Hilfe von UseTree konnten wir das Usability-Konzept unserer Anwendung erfolgreich überprüfen und optimieren. UseTree lieferte wertvolle Hinweise und Verbesserungsansätze und überzeugte uns durch eine professionelle Arbeitsweise und ein hohes Maß an Kreativität. Wir können UseTree als Dienstleister uneingeschränkt weiterempfehlen und freuen uns auf künftige gemeinsame Projekte." - André Fiedler (Projektleiter)',-1);function D(a,F,M,P,z,n){const s=o("project-header"),i=o("styled-image"),r=o("project-arrows");return f(),p("div",null,[t(s,{route:"/mechanics"}),e("div",w,[_,g,t(i,{src:n.buildURL("mechanics/mechanics_app.png"),alt:"Tablet with app and engine"},null,8,["src"]),b,v,t(i,{src:n.buildURL("mechanics/mechanics_interview.png"),alt:"On-the-job interview with mechanic"},null,8,["src"]),k,y,T,j,t(i,{src:n.buildURL("mechanics/workflow.png"),alt:"Diagram of the application's workflow"},null,8,["src"]),U,L,x,R,I,A]),t(r,{route:"/mechanics"})])}const V=u(m,[["render",D]]);export{V as default};
