import{p as l,P as d}from"./ProjectArrows-BZ8S2A5p.js";import{S as c}from"./StyledImage-BU-Vxond.js";import{b as h}from"./index-LVFgRGvP.js";import{_ as p,c as u,b as t,a as e,r as s,k as m,e as w,o as f}from"./index-CNHz_Ykd.js";const g={components:{"project-header":d,"styled-image":c,"project-arrows":l},methods:{buildURL(n){return h(n)}}},_={class:"responsive body1"},v=e("h4",null,"TL;DR",-1),b=e("p",null,"I led a discovery project aiming to improve a core functionality in a web application. I interviewed customers and internal stakeholders. As a result a couple of smaller fixes and a big feature were developed.",-1),y=e("h4",null,"Problem statement",-1),k=e("p",null,[e("a",{class:"link",href:"https://www.valsight.com/"},"Valsight"),w(" offers a financial planning solution for big companies. One of the core features is the “scenario manager” where you can create and modify assumptions (+10% more sales next year) and add them to scenarios (best case). Historically grown, it was very complex to understand and use. My task was to talk to users and figure out a way to make it easier to understand and better to use.")],-1),I=e("p",null,"The initial timeline for the project was set to 3 weeks. I proposed a project plan with three phases starting with a research phase, then an ideation/creation phase and finally a validation phase. In the end we wanted to have wireframes that we could create stories for and make part of a sprint. In the following, I will focus on the way I conducted the customer interviews and condensed the findings.",-1),j=m('<h4>Research</h4><h5>Customer interviews</h5><p>I prepared a semi-structured interview with around 10 questions asking about</p><ul><li>The current usage of the scenario manager</li><li>What worked well and what did not</li><li>What information was important to them and what not</li><li>How they worked with the scenario manager</li><li>Who worked with it the most and how they collaborated as a team</li></ul><p>I skipped questions that were not applicable for the participant or asked again if there was a chance to gain more insights by diving deeper into a topic.</p><p>We managed to recruit customers from four companies and I talked to three internal consultants. The interviews were audio recorded and transcribed later.</p><h5>Condense findings</h5><p>I printed out the interview protocols on paper and cut them into small snips of a few sentences each. Then I started a <a class="link" href="https://www.thisisservicedesigndoing.com/methods/building-a-research-wall">data wall</a> where I grouped snips to broader topics. After that I removed smaller topics to focus on the big topics that started to emerge.</p>',8),T=e("h4",null,"Ideation/Creation",-1),x=e("h5",null,"Present findings",-1),A=e("p",null,"I presented these topics to my product manager and we decided which topics we would prioritize for now.",-1),L=e("h4",null,"Development",-1),R=e("p",null,"A couple of smaller features were developed right after concluding the validation with customers. A search functionality was added to the scenario manager to find items in the list faster. Also, a couple of visual tweaks were made to the scenario manager and a convenient keyboard shortcut for faster navigation was introduced.",-1),U=e("p",null,"We had a bigger feature idea about grouping similar assumptions together and making it easier to switch between them. This idea was further iterated and refined until it was ready to be developed and shipped.",-1),C=e("h4",null,"Learnings",-1),P=e("p",null,"In hindsight, I was proud and amazed at how often I would come back to my findings for related topics or just look at the written protocols if I vaguely remembered somebody having said something similar in one of the interviews. A powerful quote from a user really sticks in your head!",-1),V=e("p",null,"I also enjoyed passing back the positive feedback from the interviews to the developers. They certainly deserved it.",-1),W=e("p",null,"The project also inspired lots of people involved in building the product and served as a source of feature ideas for the future.",-1),N=e("p",null,"On the other hand, the problem statement could have been clearer. The broad scope of it allowed for a lot of valuable exploration to happen, but it also felt like it was not worth the effort at times. Changing something is very hard if it has been at the core of an application for many years. Usually, there are good reasons why it is the way it is and users are not happy to have their workflow changed.",-1);function S(n,q,B,D,H,a){const r=s("project-header"),o=s("styled-image"),i=s("project-arrows");return f(),u("div",null,[t(r,{route:"/scenariomanager"}),e("div",_,[v,b,y,k,I,t(o,{src:a.buildURL("scenariomanager/project-plan.png"),alt:"Plan for the project"},null,8,["src"]),j,t(o,{src:a.buildURL("scenariomanager/snip-gif.mp4"),alt:"Data wall with snips moving into bigger topics"},null,8,["src"]),T,x,A,t(o,{src:a.buildURL("scenariomanager/mass-ass-slide.jpg"),alt:"Mass of assumptions was one bigger topic"},null,8,["src"]),L,R,U,t(o,{src:a.buildURL("scenariomanager/variants.jpg"),alt:'Assumptions are grouped using "variants"'},null,8,["src"]),C,P,V,W,N]),t(i,{route:"/scenariomanager"})])}const F=p(g,[["render",S]]);export{F as default};
